package ${packagename};
use base DE_EPAGES::Core::API::Test;
use Test::More;
use strict;

sub test {
    my $$self = shift;
    $$self->test_Something();
    done_testing();
}

sub test_Something {
    my $$self = shift;
    my $$SubTestName = (caller(0))[3];
    ok(1,"$$SubTestName - selftest");
}

__PACKAGE__->runScript();
