    print 'Store: '.$$StoreName.": Cartridge ${Cartridge} is not installed.\n" if ($$printOutput);
    return;
}

my $$dbi = GetCurrentDBHandle();
my $$sql = <<SQL;
select sa.attributeobjectid, sa.value 
from stringattribute sa
inner join attribute a on a.attributeid=sa.attributeid
inner join object co on co.objectid=a.attributeclassid
inner join object ao on ao.objectid=a.attributeid
where co.alias='${Class}'
and ao.alias='${Attribute}'
SQL

my $$dbResult = $$dbi->execute($$sql);
foreach my $$Row (@$$dbResult) {
    ${cursor}
}

