# §state        public
# §description  TLEHandler '${TLEHandler}'. Respoonsible for TLE function ${MyTLE}
#========================================================================================
package ${Package}::API::TLE::${TLEHandler};

use strict;

sub new {
    my $$class = shift;
    my %args  = @_;
    my $$self  = bless {}, $$class;
    return $$self;
}

sub ${MyTLE} {
    my $$self = shift; 
    my ($$Processor, $$aParams) = @_; 

    ${cursor}
}

sub RegisterHandlerProc { 
    my ($$Params) = @_; 
    __PACKAGE__->new()->register( $$Params->{'Processor'} );
    return; 
} 

sub register { 
    my $$self = shift; 
    my ($$Processor) = @_; 
    $$Processor->registerHandler('FunctionHandler', $$self, '${MyTLE}');
    return; 
} 

1;

