sub _create${cursor} {
    my $$self = shift;

    my $$hAttributes = {
        'Alias' => 'MyShop'
    };
    my $$Mockify = DE_EPAGES::Test::Mock::MockifyEpagesObject->new(
        'DE_EPAGES::Object::API::Object::Object',
        $$hAttributes
    );

    return $$Mockify->getMockObject();
}

sub _createMocks {
    my $$self = shift;

    my $$Mockery = DE_EPAGES::Test::Mock::Mockery->new();

    $$Mockery->register('SUT', [], sub {
        my $$Mockify = DE_EPAGES::Test::Mock::Mockify->new('Some::Object', []);
        return $$Mockify->getMockObject();
    });
    $$Mockery->register('Mock1', ['dependency'], sub {
        my ($$Dependency) = @_;
        my $$Object = DE_EPAGES::Test::Mock::EpagesObject->new('DE_EPAGES::Object::API::Object::Object', {
            'Alias' => 'TestObject',
        });
        return $$Object;
    });

    return $$Mockery->assemble();
}

sub test {
    my $$self = shift;
